.CODE
MAIN PROC FAR
    MOV AX , @DATA
    MOV DS , AX
;---------- Graphics Mode configuration ---------;
	mov ax, 4F02h 
	mov bx, 0100h	
	INT 10h      	;execute the configuration
;--------- if these configurations executed twice else where in the code all previous drawings will be lost ------;
    call holsterPlayerOne    
    call holsterPlayerTwo    
   
MAIN ENDP	

;-----------Clear screen  -----
Clear_screen PROC
      mov ax,0600h
      mov bh,07
      mov cx,0000
      mov dx,184Fh  ;to Row=18H col=4Fh (right bottom)
      int 10H
      RET
Clear_screen endp
;-----------set cursor in screen center -----
Cursor PROC
        mov ah,02
        mov bh,00
        mov dl,39
        mov dh,12
        int 10h
        RET
Cursor endp

holsterPlayerOne PROC 
	       ;mov ax, 4F02h    ;
	       ;mov bx, 0100h    ; 640x400 screen graphics mode
	       ;INT 10h      	;execute the configuration
	       MOV AH,0Bh   	;set the configuration
	       MOV CX, Xposition  	;set the start drawing point 
	       ADD CX, imgW  	;set the width (X) up to 64 (based on image resolution)
	       MOV DX, Yposition 	;set the hieght (Y) up to 64 (based on image resolution)
	       ADD DX, imgH 	;set the hieght (Y) up to 64 (based on image resolution)
		   mov DI, offset p1Holstered  ; to iterate over the pixels
	       jmp Start    	;Avoid drawing before the calculations
	Drawit:
	       MOV AH,0Ch   	;set the configuration to writing a pixel
           mov al, [DI]     ; color of the current coordinates
	       MOV BH,00h   	;set the page number
	       INT 10h      	;execute the configuration
	Start: 
		   inc DI
	       DEC Cx       	;  loop iteration in x direction
	       cmp Cx,Xposition         ;JNZ Drawit      	;  check if we can draw c urrent x and y and excape the y iteration
	       JNZ Drawit
           ADD Cx, imgW 	;  if loop iteration in y direction, then x should start over so that we sweep the grid
	       DEC DX       	;  loop iteration in y direction
	       cmp DX,Yposition
           JZ  ENDING   	;  both x and y reached 00 so end program
		   Jmp Drawit

	ENDING:
	RET
holsterPlayerOne ENDP

holsterPlayerTwo PROC 
	       ;mov ax, 4F02h    ;
	       ;mov bx, 0100h    ; 640x400 screen graphics mode
	       ;INT 10h      	;execute the configuration
	       MOV AH,0Bh   	;set the configuration
	       MOV CX, XptwoPosition  	;set the start drawing point 
	       ADD CX, imgW  	;set the width (X) up to 64 (based on image resolution)
	       MOV DX, YptwoPosition 	;set the hieght (Y) up to 64 (based on image resolution)
	       ADD DX, imgH 	;set the hieght (Y) up to 64 (based on image resolution)
		   mov DI, offset p2Holstered  ; to iterate over the pixels
	       jmp StartTwo    	;Avoid drawing before the calculations
	DrawitTwo:
	       MOV AH,0Ch   	;set the configuration to writing a pixel
           mov al, [DI]     ; color of the current coordinates
	       MOV BH,00h   	;set the page number
	       INT 10h      	;execute the configuration
	StartTwo: 
		   inc DI
	       DEC Cx       	;  loop iteration in x direction
	       cmp Cx,XptwoPosition         ;JNZ Drawit      	;  check if we can draw c urrent x and y and excape the y iteration
	       JNZ DrawitTwo
           ADD Cx, imgW 	;  if loop iteration in y direction, then x should start over so that we sweep the grid
	       DEC DX       	;  loop iteration in y direction
	       cmp DX,YptwoPosition
	       JZ  ENDINGTwo   	;  both x and y reached 00 so end program
		   Jmp DrawitTwo

	ENDINGTwo:
    RET
holsterPlayerTwo ENDP

end main 



          
